import React, { useEffect } from 'react';
import './App.css'; // Make sure your CSS file has the necessary styles

function App() {
  // Using useEffect to handle the jQuery interactions when the component mounts
  useEffect(() => {
    // jQuery for login button animation
    $('#login-button').click(function() {
      $('#login-button').fadeOut("slow", function() {
        $("#container").fadeIn();
        TweenMax.from("#container", .4, { scale: 0, ease: Sine.easeInOut });
        TweenMax.to("#container", .4, { scale: 1, ease: Sine.easeInOut });
      });
    });

    // jQuery for close button animation
    $(".close-btn").click(function() {
      TweenMax.from("#container", .4, { scale: 1, ease: Sine.easeInOut });
      TweenMax.to("#container", .4, { left: "0px", scale: 0, ease: Sine.easeInOut });
      $("#container, #forgotten-container").fadeOut(800, function() {
        $("#login-button").fadeIn(800);
      });
    });

    // Forgotten password click event
    $('#forgotten').click(function() {
      $("#container").fadeOut(function() {
        $("#forgotten-container").fadeIn();
      });
    });
  }, []); // Empty dependency array means it runs once when the component is mounted

  return (
    <div className="App">
      {/* Login button */}
      <button id="login-button" className="login-btn">
        Login
      </button>

      {/* Container for login form */}
      <div id="container" className="container" style={{ display: 'none' }}>
        <h2>Login Form</h2>
        <form>
          {/* Your form fields here */}
        </form>
        <button className="close-btn">Close</button>
      </div>

      {/* Forgotten password form */}
      <div id="forgotten-container" className="forgotten-container" style={{ display: 'none' }}>
        <h2>Forgotten Password</h2>
        <form>
          {/* Your forgotten password fields */}
        </form>
      </div>
    </div>
  );
}

export default App;
